enchant.ENV.USE_TOUCH_TO_START_SCENE = false;

var app = new (function(){

	this.rootpath = "/pz";

	this.assets = {};
	this.assets.loader = "data:image/gif;base64,";
	this.assets.floating = { position: "absolute", zIndex: "10", left: "0px", top: "0px", width: window.innerWidth + "px", height: window.innerHeight + "px" };

	this.cover = function(){
		$("#cover").remove();
		$("body").append($("<div>").attr("id", "cover").css(
			$.extend({}, this.assets.floating,
				{ zIndex: 70, background: "#FFFFFF no-repeat center center", backgroundImage: "url(" + this.assets.loader + ")" })
		));
	};

	this.discover = function(){
		$("#cover").remove();
	};

	this.trace = function(text){
		var trace = $("#trace");
		if (trace.length == 0) {
			this._trace_text = [];
			trace = $("<div>").attr("id", "trace").css(
				$.extend({}, this.assets.floating,
					{ zIndex: 80, font: "16px/1.4 monospace" })
			);
			$("body").append(trace);
		}
		this._trace_text.push(text);
		if (10 < this._trace_text.length) {
			this._trace_text.shift();
		}
		trace.html(this._trace_text.join("<br>"));
	};

	this.run = function(){
		var stage = $("#enchant-stage");
		this.game = new enchant.Core(stage.width(), stage.height());
		this.game.fps = 30;
		this.game.rootScene.backgroundColor = "#000000";
		this.game.onerror = (function(_this){
			return function(e){
				(_this.game.currentScene.onerror || function(){}).call(_this.game.currentScene, e);
			};
		})(this);

		this._control = $("<div>").attr("id", "control").css(
			$.extend({}, this.assets.floating, { zIndex: 90 })
		);
		this._hammer = new Hammer(this._control.get(0));
		this._hammer.get("swipe").set({direction: Hammer.DIRECTION_ALL});
		this._hammer.get("press").set({time: 500});
		this._hammer.on("tap doubletap press swipeleft swiperight swipeup swipedown", (function(_this){
			return function(e){
				(_this.game.currentScene["on" + e.type] || function(){}).call(_this.game.currentScene, e);
			};
		})(this));
		$("body").append(this._control);

		this.game.start();

		for (var i = 0; i < arguments.length; i++) {
			this.game.pushScene(new (arguments[i])());
		}

		$("meta[name=viewport]").remove();
		$("head").append($("<meta>").attr("name", "viewport").attr("content", "width=device-width,initial-scale=1.0,minimum-scale=1.0,user-scalable=no"));
	};

	this.makeId = function(){
		this._make_id = (this._make_id || 0);
		return this._make_id++;
	};

	this.loadSprite = function(name, callback){
		var path = this.rootpath + "/sprite/" + name;
		this.game.load(path, path, callback);
	};

	this.getSprite = function(name){
		var path = this.rootpath + "/sprite/" + name;
		return this.game.assets[path];
	};

	this.loadMap = function(name, callback){
		var path = this.rootpath + "/maps/" + name;
		$.ajax({ url: path, dataType: "xml"}).done(callback);
	};

	this.parseCSV = function(text){
		var csv = [];
		var rows = text.split(/\r\n|\r|\n/);
		for (var i = 0; i < rows.length; i++) {
			if (rows[i].indexOf(",") != -1) {
				var cols = rows[i].split(",");
				for (j = 0; j < cols.length; j++) {
					cols[j] = parseInt(cols[j]);
				}
				csv.push(cols);
			}
			else if (rows[i] != "") {
				csv.push(parseInt(rows[i]));
			}
		}
		return csv;
	};
});

