enchant.Mob32x32 = enchant.Class.create(enchant.Group, {
	initialize: function(x, y, option){
		enchant.Group.call(this);

		this.nodeId = app.makeId();
		this.nodeName = (option.nodeName || "Mob32x32");

		this.ready = false;
		this.direction = 0;

		this.option = {};
		this.option.chipname = null;
		this.option.shadow = null;
		this.option.adjust = { x: -8, y: -18, shadow_y: 1 };
		this.option = $.extend(this.option, (option || {}));

		this.x = x * 16
		this.y = y * 16

		this._loaded = 0;
		this._loaded_timer = null;

		this._shadow = new enchant.Sprite(32, 32);
		this._shadow.x = this.option.adjust.x;
		this._shadow.y = this.option.adjust.y + this.option.adjust.shadow_y;
		this.addChild(this._shadow);

		this._sprite = new enchant.Sprite(32, 32);
		this._sprite.x = this.option.adjust.x;
		this._sprite.y = this.option.adjust.y;
		this.addChild(this._sprite);

		app.loadSprite(this.option.shadow, (function(_this){
			return function(){
				_this._loaded += 50;
				_this._shadow.image = app.getSprite(_this.option.shadow);
				_this._shadow.visible = false;
				_this._shadow.frame = 1;
				setTimeout(function(){
					_this._shadow.frame = 0;
					_this._shadow.visible = true;
				}, 1);
			};
		})(this));

		app.loadSprite(this.option.chipname, (function(_this){
			return function(){
				_this._loaded += 50;
				_this._sprite.image = app.getSprite(_this.option.chipname);
				_this._sprite.visible = false;
				_this._sprite.frame = 1;
				setTimeout(function(){
					_this._sprite.frame = 0;
					_this._sprite.visible = true;
				}, 1);
			};
		})(this));

		this._loaded_timer = setInterval((function(_this){
			return function(){
				if (100 <= _this._loaded) {
					clearTimeout(_this._loaded_timer);
					_this._loaded_timer = null;

					(_this.onloaded || function(){}).call(_this);
					_this.ready = true;
				}
			};
		})(this), 500);
	}
});

enchant.Cursor = enchant.Class.create(enchant.Sprite, {
	initialize: function(){
		enchant.Sprite.call(this, 16, 16);

		this.assets = { cursor: "cursor01.png" };

		this.visible = false;

		this._visible_timer = null;
		this._map_x = 0;
		this._map_y = 0;

		app.loadSprite(this.assets.cursor, (function(_this){
			return function(){
				_this.image = app.getSprite(_this.assets.cursor);
			};
		})(this));
	},
	set: function(x, y){
		this._map_x = x;
		this._map_y = y;

		this.visible = true;
		this.x = x * 16;
		this.y = y * 16;

		clearTimeout(this._visible_timer);
		this._visible_timer = setTimeout((function(_this){
			return function(){
				_this.visible = false;
			};
		})(this), 1000);
	},
	get: function(){
		return { x: this._map_x, y: this._map_y };
	}
});

enchant.Stage = enchant.Class.create(enchant.Scene, {
	initialize: function(option){
		enchant.Scene.call(this);

		app.cover();

		this.nodeId = app.makeId();
		this.nodeName = (option.nodeName || "Stage");

		this.option = {};
		this.option.chipname = null;
		this.option.mapdata = null;
		this.option = $.extend(this.option, (option || {}));

		this._loaded = 0;
		this._loaded_timer = null;

		this.basegroup = new enchant.Group();

		this.background = new enchant.Map(16, 16);
		this.basegroup.addChild(this.background);

		this.cursor = new enchant.Cursor();
		this.basegroup.addChild(this.cursor);

		this.mobsgroup = new enchant.Group();
		this.basegroup.addChild(this.mobsgroup);

		this.player = null;
		this.mobs = [];

		this.foreground = new enchant.Map(16, 16);
		this.basegroup.addChild(this.foreground);

		this.addChild(this.basegroup);

		this.data = new enchant.Data();
		this.addChild(this.data);

		app.loadSprite(this.option.chipname, (function(_this){
			return function(){
				_this._loaded += 50;
				_this.background.image = app.getSprite(_this.option.chipname);
				_this.foreground.image = app.getSprite(_this.option.chipname);
			};
		})(this));

		app.loadMap(this.option.mapdata, (function(_this){
			return function(data){
				_this._loaded += 50;

				_this.background.loadData(
					app.parseCSV($(data).find("layer[name=background] data").text()),
					app.parseCSV($(data).find("layer[name=mono] data").text()));

				_this.background.collisionData =
					app.parseCSV($(data).find("layer[name=collision] data").text());

				_this.foreground.loadData(
					app.parseCSV($(data).find("layer[name=foreground] data").text()));

				var event = app.parseCSV($(data).find("layer[name=event] data").text());
				for (var y = 0; y < event.length; y++) {
					for (var x = 0; x < event[y].length; x++) {
						(_this.dispatchMobs || function(){}).call(_this, x, y, event[y][x]);
					}
				}

				for (var i = 0; i < _this.mobs.length; i++) {
					_this.mobsgroup.addChild(_this.mobs[i]);
				}
				_this.mobsgroup.addChild(_this.player);
			};
		})(this));

		this._loaded_timer = setInterval((function(_this){
			return function(){
				if (100 <= _this._loaded) {
					clearTimeout(_this._loaded_timer);
					_this._loaded_timer = null;

					(_this.onloaded || function(){}).call(_this);
					_this.ready = true;

					_this.basegroup.onenterframe = function(){ _this._onenterframe.call(_this); };

					app.discover();
				}
			};
		})(this), 500);
	},
	hitTest: function(x, y){
		var map = { x: Math.floor(x / 16), y: Math.floor(y / 16) };

		if (this.hitTestBlock(map.x, map.y)) {
			return true;
		}
		return false;
	},
	hitTestBlock: function(x, y){
		if (typeof this.background.collisionData[y] == "undefined") {
			return true;
		}
		else if (typeof this.background.collisionData[y][x] == "undefined") {
			return true;
		}
		else if (this.background.collisionData[y][x]) {
			return true;
		}
		return false;
	},
	hitTestMobs: function(x, y){
		for (var i = 0; i < this.mobs.length; i++) {
			if (Math.floor(this.mobs[i].x / 16) == x && Math.floor(this.mobs[i].y / 16) == y) {
				return true;
			}
		}
		return false;
	},
	hitTestPlayer: function(x, y){
		if (Math.floor(this.player.x / 16) == x && Math.floor(this.player.y / 16) == y) {
			return true;
		}
		return false;
	},
	_onenterframe: function(){
		var x = Math.min((app.game.width  - 32) / 2 - this.player.x, 0);
		var y = Math.min((app.game.height - 32) / 2 - this.player.y, 0);
		x = Math.max(app.game.width,  x + this.background.width)  - this.background.width;
		y = Math.max(app.game.height, y + this.background.height) - this.background.height;
		this.basegroup.x = Math.round(x);
		this.basegroup.y = Math.round(y);
	},
	ontap: function(e){
		var x = Math.floor((e.center.x - ((window.innerWidth - 320)  / 2) - this.basegroup.x) / 16);
		var y = Math.floor((e.center.y - ((window.innerHeight - 320) / 2) - this.basegroup.y) / 16);

		this.cursor.set(x, y);
		this.cursor.frame = (this.hitTestMobs(x, y) ? 1 : 0);

		(this.player.ontap || function(){}).call(this.player, this.cursor.get());
	}
});

var PlainStage = enchant.Class.create(enchant.Stage, {
	initialize: function(){
		enchant.Stage.call(this, { nodeName: "PlainStage", chipname: "map01.png", mapdata: "plain.xml" });
	},
	dispatchMobs: function(x, y, mobs_id){
		if (mobs_id == 32) {
			this.player = new Minami(x, y);
		}
		else if (mobs_id == 33) {
			this.mobs.push(new Slime(x, y));
		}
		else if (mobs_id == 34) {
			this.mobs.push(new Magician(x, y));
		}
	},
	onattack: function(attack){
app.trace(attack + "ダメージ");
		this.data._hpbar_fore.width -= attack;
		if (this.data._hpbar_fore.width < 0){
			this.data._hpbar_fore.width = 0;
		}
	}
});

var Minami = enchant.Class.create(enchant.Mob32x32, {
	initialize: function(x, y){
		enchant.Mob32x32.call(this, x, y, { nodeName: "Minami", chipname: "chara01.png", shadow: "shadow01.png" });

		this._cursor = null;

		this._walk_speed = Math.floor(app.game.fps * 300 / 1000);
	},
	onenterframe: function(){
		if (this.ready) {
			var input = $.extend({}, app.game.input);
			var vx = 0, vy = 0;

			if (input.up || input.down || input.left || input.right) {
				this._cursor = null;
			}

			if (this._cursor != null) {
				var map = { x: Math.floor(this.x / 16), y: Math.floor(this.y / 16) };

				input.up    = (this._cursor.y < map.y);
				input.down  = (this._cursor.y > map.y);
				input.left  = (this._cursor.x < map.x);
				input.right = (this._cursor.x > map.x);

				if (this._cursor.x == map.x && this._cursor.y == map.y) {
					this._cursor = null;
				}
			}

			if (input.up) {
				this.direction = 3;
				vy = -16;
				if (app.game.currentScene.hitTest(this.x, this.y + vy)) {
					vy = 0;
				}
			}
			if (input.down) {
				this.direction = 0;
				vy = +16;
				if (app.game.currentScene.hitTest(this.x, this.y + vy)) {
					vy = 0;
				}
			}
			if (input.left) {
				this.direction = 1;
				vx = -16;
				if (app.game.currentScene.hitTest(this.x + vx, this.y)) {
					vx = 0;
				}
			}
			if (input.right) {
				this.direction = 2;
				vx = +16;
				if (app.game.currentScene.hitTest(this.x + vx, this.y)) {
					vx = 0;
				}
			}

			if (vx != 0 && vy != 0) {
				if (app.game.currentScene.hitTest(this.x + vx, this.y + vy)) {
					if (!app.game.currentScene.hitTest(this.x + vx, this.y)) {
						vy = 0;
					}
					else if (!app.game.currentScene.hitTest(this.x, this.y + vy)) {
						vx = 0;
					}
					else {
						vx = 0;
						vy = 0;
					}
				}
			}

			if (vx == 0 && vy == 0) {
				this._cursor = null;
			}

			if (vx != 0 || vy != 0) {
				this.ready = false;
				this.tl.moveTo(this.x + vx, this.y + vy, this._walk_speed);
				this.tl.exec((function(_this){
					return function(){
						_this.x = Math.round(_this.x);
						_this.y = Math.round(_this.y);
						_this.ready = true;
					}
				})(this));
			}
		}

		var i = this.direction * 3;
		this._sprite.frame = [
			i,     i,     i,     i,     i,     i,     i,     i,     i,
			i + 1, i + 1, i + 1, i + 1, i + 1, i + 1, i + 1, i + 1, i + 1,
			i + 2, i + 2, i + 2, i + 2, i + 2, i + 2, i + 2, i + 2, i + 2,
			i + 1, i + 1, i + 1, i + 1, i + 1, i + 1, i + 1, i + 1, i + 1];
	},
	ontap: function(e){
		this._cursor = e;
	}
});

var Slime = enchant.Class.create(enchant.Mob32x32, {
	initialize: function(x, y){
		enchant.Mob32x32.call(this, x, y, { nodeName: "Slime", chipname: "enemy01.png", shadow: "shadow01.png" });

		this._automove_index = 0;
		this._automove = [];

		this._walk_speed = Math.floor(app.game.fps * 500 / 1000);
		this._attack_speed = Math.floor(app.game.fps * 300 / 1000);
		this._attack_delay = Math.floor(app.game.fps * 2000 / 1000);

		this._attack_range = [1, 1, 2, 2, 3, 4, 5];

		for (var i = 0; i < 10; i++) {
			var pattern = Math.floor(Math.random() * 4);

			if (pattern == 0) {
				this._automove.push({ vx:  16, vy:   0 });
			}
			else if (pattern == 1) {
				this._automove.push({ vx:   0, vy:  16 });
			}
			else if (pattern == 2) {
				this._automove.push({ vx: -16, vy:   0 });
			}
			else if (pattern == 3) {
				this._automove.push({ vx:   0, vy: -16 });
			}
		}
	},
	onenterframe: function(){
		if (this.ready) {
			var input = { up: false, down: false, left: false, right: false };
			var vx = 0, vy = 0;

			var x		= Math.floor(this.x / 16);
			var y		= Math.floor(this.y / 16);
			var left	= Math.floor((this.x - 16) / 16);
			var up		= Math.floor((this.y - 16) / 16);
			var right	= Math.floor((this.x + 16) / 16);
			var down	= Math.floor((this.y + 16) / 16);

			if (app.game.currentScene.hitTestPlayer(x, y)) {
				this.ready = false;
				var attack = this._attack_range[Math.floor(Math.random() * this._attack_range.length)];
				this.tl.moveBy(0, -8, this._attack_speed);
				this.tl.moveBy(0, +8, this._attack_speed);
				this.tl.exec(function(){
					(app.game.currentScene["onattack"] || function(){}).call(app.game.currentScene, attack);
				});
				this.tl.delay(this._attack_delay);
				this.tl.exec((function(_this){
					return function(){
						_this.x = Math.round(_this.x);
						_this.y = Math.round(_this.y);
						_this.ready = true;
					};
				})(this));
			}
			else if (app.game.currentScene.hitTestPlayer(left, y)) {
				this.ready = false;
				var attack = this._attack_range[Math.floor(Math.random() * this._attack_range.length)];
				this.tl.moveBy(-8, 0, this._attack_speed);
				this.tl.moveBy(+8, 0, this._attack_speed);
				this.tl.exec(function(){
					(app.game.currentScene["onattack"] || function(){}).call(app.game.currentScene, attack);
				});
				this.tl.delay(this._attack_delay);
				this.tl.exec((function(_this){
					return function(){
						_this.x = Math.round(_this.x);
						_this.y = Math.round(_this.y);
						_this.ready = true;
					};
				})(this));
			}
			else if (app.game.currentScene.hitTestPlayer(x, up)) {
				this.ready = false;
				var attack = this._attack_range[Math.floor(Math.random() * this._attack_range.length)];
				this.tl.moveBy(0, -8, this._attack_speed);
				this.tl.moveBy(0, +8, this._attack_speed);
				this.tl.exec(function(){
					(app.game.currentScene["onattack"] || function(){}).call(app.game.currentScene, attack);
				});
				this.tl.delay(this._attack_delay);
				this.tl.exec((function(_this){
					return function(){
						_this.x = Math.round(_this.x);
						_this.y = Math.round(_this.y);
						_this.ready = true;
					};
				})(this));
			}
			else if (app.game.currentScene.hitTestPlayer(right, y)) {
				this.ready = false;
				var attack = this._attack_range[Math.floor(Math.random() * this._attack_range.length)];
				this.tl.moveBy(+8, 0, this._attack_speed);
				this.tl.moveBy(-8, 0, this._attack_speed);
				this.tl.exec(function(){
					(app.game.currentScene["onattack"] || function(){}).call(app.game.currentScene, attack);
				});
				this.tl.delay(this._attack_delay);
				this.tl.exec((function(_this){
					return function(){
						_this.x = Math.round(_this.x);
						_this.y = Math.round(_this.y);
						_this.ready = true;
					};
				})(this));
			}
			else if (app.game.currentScene.hitTestPlayer(x, down)) {
				this.ready = false;
				var attack = this._attack_range[Math.floor(Math.random() * this._attack_range.length)];
				this.tl.moveBy(0, +8, this._attack_speed);
				this.tl.moveBy(0, -8, this._attack_speed);
				this.tl.exec(function(){
					(app.game.currentScene["onattack"] || function(){}).call(app.game.currentScene, attack);
				});
				this.tl.delay(this._attack_delay);
				this.tl.exec((function(_this){
					return function(){
						_this.x = Math.round(_this.x);
						_this.y = Math.round(_this.y);
						_this.ready = true;
					};
				})(this));
			}
			else {
				if (typeof this._automove[this._automove_index] != "undefined") {
					input.up    = (this._automove[this._automove_index].vy < 0);
					input.down  = (this._automove[this._automove_index].vy > 0);
					input.left  = (this._automove[this._automove_index].vx < 0);
					input.right = (this._automove[this._automove_index].vx > 0);
				}

				if (input.up) {
					this.direction = 3;
					vy = -16;
					if (app.game.currentScene.hitTest(this.x, this.y + vy)) {
						vy = 0;
					}
				}
				if (input.down) {
					this.direction = 0;
					vy = +16;
					if (app.game.currentScene.hitTest(this.x, this.y + vy)) {
						vy = 0;
					}
				}
				if (input.left) {
					this.direction = 1;
					vx = -16;
					if (app.game.currentScene.hitTest(this.x + vx, this.y)) {
						vx = 0;
					}
				}
				if (input.right) {
					this.direction = 2;
					vx = +16;
					if (app.game.currentScene.hitTest(this.x + vx, this.y)) {
						vx = 0;
					}
				}

				if (vx != 0 && vy != 0) {
					if (app.game.currentScene.hitTest(this.x + vx, this.y + vy)) {
						if (!app.game.currentScene.hitTest(this.x + vx, this.y)) {
							vy = 0;
						}
						else if (!app.game.currentScene.hitTest(this.x, this.y + vy)) {
							vx = 0;
						}
						else {
							vx = 0;
							vy = 0;
						}
					}
				}

				if (vx != 0 || vy != 0) {
					this.ready = false;
					this.tl.moveTo(this.x + vx, this.y + vy, this._walk_speed);
					this.tl.exec((function(_this){
						return function(){
							_this.x = Math.round(_this.x);
							_this.y = Math.round(_this.y);
							_this.ready = true;
						};
					})(this));
				}

				if (vx == 0 && vy == 0) {
					this.ready = false;
					this.tl.delay(this._walk_speed);
					this.tl.exec((function(_this){
						return function(){
							_this.x = Math.round(_this.x);
							_this.y = Math.round(_this.y);
							_this.ready = true;
						};
					})(this));
				}

				this._automove_index++;
				if (this._automove.length < this._automove_index) {
					this._automove_index = 0;
				}
			}
		}

		var i = this.direction * 3;
		this._sprite.frame = [
			i,     i,     i,     i,     i,     i,     i,     i,     i,
			i + 1, i + 1, i + 1, i + 1, i + 1, i + 1, i + 1, i + 1, i + 1,
			i + 2, i + 2, i + 2, i + 2, i + 2, i + 2, i + 2, i + 2, i + 2];
	}
});

